sys_sg_write_back_action_item.execution_script:
// create incident from asset
(function WriteBackAction(input) {
	var gr = new GlideRecord('incident');
	gr.initialize();
	gr.setValue('short_description', input.short_description);
	gr.setValue('caller_id', input.caller_id);
	gr.setValue('category','Hardware');
	//gr.setValue('subcategory',input.subcategory);
	//gr.setValue('urgency', input.urgency);
	//gr.setValue('contact_type', 'phone');
	gr.setValue('description', gs.getMessage('Problem with "{0}"', input.display_name));
	if(!gs.nil( input.cmdb_ci))
		gr.setValue('cmdb_ci', input.cmdb_ci);
	gr.insert();
})(input);
